parameters.jsCode:
// Count successful and failed reports
const items = $input.all();
const totalOrgs = items.length;
const successful = items.filter(i => i.json.status !== 'failed').length;
const failed = totalOrgs - successful;

return {
  json: {
    period: $('Prepare Report Data').item.json.period,
    monthName: $('Prepare Report Data').item.json.monthName,
    totalOrganizations: totalOrgs,
    successfulReports: successful,
    failedReports: failed,
    timestamp: new Date().toISOString()
  }
};

